generated.css:
#navigation{
    background-color:rgb(88, 189, 189);
     top:0;
    font-family: 'Galada', cursive;
     position: sticky;
     padding:30px;
}

#navigation a{
    color:white;
    text-decoration: none;
    font-size: 20px;
    position: sticky;
top:0;
}

#navigation > ul {
list-style: none; 
margin: 0; 
padding: 30px;  
padding-left: 0px;
display: flex;
justify-content: space-around;
position: sticky;
top:0;
}

#nav{
    margin-right: 10px;
}

#navigation .container{
    margin: 0 auto;
}

#navigation li{
position: relative;
}

#navigation li ul{
    background-color: black;
    color: white;
    position: absolute;
    top: 20px;
    left: -3px;
    padding: 16px;
    display:none;

}

#navigation a:hover + ul,
#navigation a + ul:hover{
    flex-direction: column;
    display:flex;
    
}


#root{
grid-template-rows: min-content min-content auto min-content ;
    background: gray;
    display: grid;
    grid-template-areas: 
    "navigation"
    "header"
    "content"
    "footer";
    min-height: 100vh;
}

#header {
    padding: 10px;
    margin: 50px;
  
}

#header .container{
    margin: 0 auto;
}

#content{
    padding: 10px;
  background: gray;
}

#content p:first-child{
    background: teal;
}

#content p:nth-child(3){
    background: white;
}

#content p:nth-child(2n){
    background:purple;
}

#content .container{
    margin: 0 auto;
}

#footer{
    padding: 10px;
   color:white;
   background: black;
 
}

#footer a {
    color: white;
    font-family: 'Pacifico', cursive;
    text-decoration: none;
}

#footer > ol {
list-style: none; 
margin: 0; 
padding: 30px;  
padding-left: 0px;
}

#footer > ol > li{
    display: inline;
    margin-right: 10px;
}

h1 {
    font-size: 30px;
}

p {
    font-size: 20px;
}

h2{
    font-family: 'Pacifico', cursive;
    font-size: 30px;
}

#bold{
    font-weight: bold;
}

img {
width: 50%;
max-width: 500px;
}

.container {
 max-width: 700px;
    margin: 0 auto;
}

h3{
    font-family: 'Noto Serif', serif; 
}

.fa-envelope:before{
    font-size: 30px;
}
.fa-instagram:before {
    font-size: 30px;
 
}

.fa-facebook:before{
    font-size: 30px;
   
}

.fa-home:before {
   
    font-size: 30px;
}
@media(min-width: 612px){
    body{
      grid-template-columns:  150px auto;
      grid-template-rows:  min-content auto min-content;
        grid-template-areas:
        "navigation header"
        " navigation content"
        "navigation footer";
       
    }
    #navigation {
       grid-area: navigation; 
        width: 20%;
        height: 100vh;
    }
    #navigation > ul {
        flex-direction: column;
        height:75vh;
       
    }
}
